import { useState, useEffect } from 'react';
import BlogList from './BlogList';

const Home = () => {

    //useState is a way to store data in the browser
    const [blogs, setBlogs] = useState([
        { title: 'My new website', body: 'lorem ipsum...', author: 'mario', id: 1 },
        { title: 'Welcome party!', body: 'lorem ipsum...', author: 'yoshi', id: 2 },
        { title: 'Web dev top tips', body: 'lorem ipsum...', author: 'mario', id: 3 }
    ]);
    const [name, setName] = useState('YOYO'); //reactive variable 'name'
    const [age, setAge] = useState(25);

    //deltes item from blog list. reference BlogList.js
    const handleDelete = (id) => {
        const newBlogs = blogs.filter(blog => (blog.id !== id))
        setBlogs(newBlogs);

    }

    const handleClick = (e) => {
        console.log('hello peeps ', e);
        setName('YoYo');
        setAge(25);
    }

    const handleClickAgain = (name, e) => {
        setName('NoNo');
        setAge(30);
        console.log('hello ' + name, e.target);
    }

    //fires everytime content renders
    useEffect(()=> {
        console.log('use effect ran');
        console.log(blogs);
    }) 

    return (
        <div className="home">
            <BlogList blogs={blogs} title='ALL BLOGS!!' handleDelete={handleDelete}/>
            <BlogList blogs={blogs.filter((blog)=>(blog.author==='mario'))} title="Mario's blogs" handleDelete={handleDelete}/>
            <h2>
                Homepage Content from Home.js
            </h2>
            <p>{name} is {age} years old</p>
            <button onClick={handleClick}>Click me</button>
            <button onClick={(e) => { handleClickAgain('steveO', e) }}>Click me again </button>
        </div>
    );
}

export default Home;
