const BlogList = ( {blogs, title, handleDelete}) => {//was props
    // const blogs = props.blogs; //not needed when the function input variable has been destructured. 
    // const title = props.title;

    return (
        <div className="blog-list">
            <h2>{title}</h2>
            {blogs.map((blog) => (
                <div className="blog-preview" key={blog.id}>
                    <h2>{blog.title}</h2>
                    <p>Written by {blog.author}</p>
                    <button onClick={() => handleDelete(blog.id)}>delete blog</button>
                </div>
            )
            )}
        </div>
    );
}

export default BlogList;
